create table if not exists book (id bigint generated by default as identity, createdTime timestamp, deleted boolean, opVersion integer, updatedTime timestamp, author varchar(255), categoryPath varchar(255), name varchar(255), primary key (id))
create table if not exists category (id bigint generated by default as identity, createdTime timestamp, deleted boolean, opVersion integer, updatedTime timestamp, level integer, name varchar(255), parentId bigint, parentPath varchar(255), primary key (id))
create table if not exists permission (id bigint generated by default as identity, createdTime timestamp, deleted boolean, opVersion integer, updatedTime timestamp, description varchar(255), name varchar(255), url varchar(255), primary key (id))
create table if not exists role (id bigint generated by default as identity, createdTime timestamp, deleted boolean, opVersion integer, updatedTime timestamp, description varchar(255), enable boolean, name varchar(255), primary key (id))
create table if not exists role_permission (roleId bigint not null, permissionId bigint not null, primary key (roleId, permissionId))
create table if not exists user (id bigint generated by default as identity, createdTime timestamp, deleted boolean, opVersion integer, updatedTime timestamp, name varchar(255), password varchar(255), realname varchar(255), status integer, primary key (id), unique key `uniq_name` (`name`))
create table if not exists user_oper_log (id bigint generated by default as identity, createdTime timestamp, deleted boolean, opVersion integer, updatedTime timestamp, ip varchar(255), method varchar(255), operation varchar(255), params varchar(255), type varchar(255), userId bigint, username varchar(255), primary key (id))
create table if not exists user_role (userId bigint not null, roleId bigint not null, primary key (userId, roleId))